# def qui englobe le tableau et return la variable
def number_handle (array)
    puts "Il y a #{array.count} handle. "
end
# .count sert a compter le nombre d'element dans le tableau
def min_handle (array)
    puts "Ce Handle est le plus court de la liste: #{array.min { |a, b| a.size <=> b.size}}."
end
# min cherche dans le tableau le plus petit handle en comparant deux varaible jusqu'a la derniere
def five_handle (array)
    puts " Il y a  #{array.find_all { |l| l.size ==6 }.length} Handle qui ont 5 caracteres" 
end
#find_all me cherche tout les variable equal a 6 . 6 - le @ donc 5 
def maj_handle (array)
    puts " il y a #{array.count {|a| a=~/^@[A-Z]/} } Handles qui commencent par une Majuscule. " 
end
 #count cherche tout les variable qui commence par une majuscule en utilisant  le regex 
def downcase_handle (array)
    puts " Attend je range proprement "
    puts array.sort{ |a, b| a.downcase <=> b.downcase}
end
#sort me sort tout les variable dans un ordre alphabetique en mettant les majuscules avec downcase 
def length_handle (array)
    puts "Et maintenant de bas en haut !"
    puts array.sort{ |a, b| a.length <=> b.length}         
end
#sort me sort tout les variable dans un ordre de largeur  en utilisant  length
def epenser_handle (array)      
    puts "@Epenser est en  #{array.index ('@epenser') } position  "
end
#index me donne la valeur de classement dans le tableau la variable @epenser

def group_handle (array)
    puts array.group_by(&:length).map{|key, value| [key-1 ,  value]}.sort
end 
#group_by utilise la notion length pour les classer en longeur puis map viens appliquer une modification de -1 pour enlever @
def process
   array = array1
   number_handle (array)
   sleep 10
   min_handle (array)
   sleep 10
   five_handle (array)
   sleep  10
   maj_handle (array)
   sleep 10
   downcase_handle (array)
   puts "Liste dnas l'ordre alphabetique"
   sleep 10
   length_handle (array)
   puts "List du plus petit au plus grand"
   sleep 10
   epenser_handle (array)
   sleep 10
   group_handle (array)
end
process
